<?phpsession_start();require_once("../connect.php");require_once("../function.php");$accounts_id = $_SESSION['accounts_id'];$code_pin = $_REQUEST['code_pin'];$packages = $_REQUEST['packages'];$code_value = $_REQUEST['code_value'];$bet = $_REQUEST['bet'];if(countquery("SELECT * FROM tbl_code WHERE code_pin='$code_pin' AND code_value='$code_value' AND code_package='$packages' AND code_bet='' ")==0){	exit("error1");}if(countquery("SELECT * FROM tbl_code WHERE code_pin='$code_pin' AND code_value='$code_value' AND code_package='$packages' AND code_bet='$bet' ")>=1){	exit("error2");}mysql_query_cheat("UPDATE tbl_code SET code_bet='$bet',code_owner='$accounts_id' WHERE code_pin='$code_pin' AND code_value='$code_value' AND code_package='$packages'");if($_SESSION['refer']!=''){$check_rate = mysql_query_cheat("SELECT * FROM tbl_rate WHERE rate_id='$packages'");$check_row  = mysqli_fetch_array_cheat($check_rate);$rebates = $check_row['rate_start'] * 0.10;//referrer$refq = mysql_query_cheat("SELECT * FROM tbl_accounts WHERE username='".$_SESSION['refer']."'");$refrow  = mysqli_fetch_array_cheat($refq);$accounts_id_referrer = $refrow['accounts_id'];//code value$codeq = mysql_query_cheat("SELECT * FROM tbl_code WHERE code_pin='$code_pin' AND code_value='$code_value' AND code_package='$packages'");$coderow  = mysqli_fetch_array_cheat($codeq);$code_id = $coderow['code_id'];//referralsmysql_query_cheat("UPDATE tbl_accounts SET balance=balance + $rebates WHERE accounts_id='$accounts_id_referrer'");mysql_query_cheat("INSERT INTO tbl_bonus SET amount='$rebates',accounts_id='$accounts_id_referrer',bonus_type='rf',refer_summary='$code_id'");}exit("done");?>